function getSearchResults (query) {
    $.ajax({
      dataType: "json",
      method: "GET",
      url: "/liman_arama",
      data: {
        search_query: query
      },
      success: function (data, status, xhr) 
      {
        if (data.length == 0) {
          $("#liman_search_results").append(`
            <a href="#">Sonu bulunamad</a>
          `);
        }

        let firstone = 0
        data.forEach(el => {
          if (firstone == 0) {
            $("#liman_search_results").append(`
              <a href="${el.url}" class="hovered">${el.name}</a>
            `);
            firstone++
          } else {
            $("#liman_search_results").append(`
              <a href="${el.url}">${el.name}</a>
            `);
          }
        });
      },
      error: function (jqXhr, textStatus, error) 
      {
        console.log(error);
      }
    })
}

function liman_search() {
  let input = $("#liman_search_input");
  let result = $("#liman_search_results");
  
  if (input.val().length > 2)
  {
    result.html("");
    getSearchResults(input.val());
    result.fadeIn(250);
  }

  if (input.val() == "") 
  {
    result.fadeOut(250);
  }
}

$(document).ready(function() {
  $("body").tooltip({ selector: '[data-toggle=tooltip]' });
  
  let input = $("#liman_search_input");
  let result = $("#liman_search_results");

  let idx = 0

  input.on("keydown", function ( e ) {
    if (e.keyCode == 13)
    {
      e.preventDefault();
      result.find(".hovered")[0].click();
    }

    if(!(e.keyCode == 38 || e.keyCode == 40 || e.keyCode == 13)) {
      clearTimeout($.data(this, 'timer'));
      let wait = setTimeout(liman_search, 150);
      $(this).data('timer', wait);
      idx = 0
    } else {
      e.preventDefault();
      if(!result.html().includes("Sonu bulunamad")) {
        let results = result.find("a")
        let len = results.length - 1
        if (e.keyCode == 38) {
          if (idx <= 0) {
            idx = 0
          } else {
            idx--
            result.find(results[idx + 1]).removeClass("hovered");
            result.find(results[idx]).addClass("hovered");
          }
        }

        if (e.keyCode == 40) {
          if (idx >= len) {
            idx = len
          } else {
            idx++
            result.find(results[idx - 1]).removeClass("hovered");
            result.find(results[idx]).addClass("hovered");
          }
        }
      }
    }
    
  })

  $(document).on("click", function(event){
    var $trigger = $("#liman_search");
    if($trigger !== event.target && !$trigger.has(event.target).length)
    {
      result.fadeOut(250);
    }            
  });
});
function upload(option) {
  if (typeof option.file === "undefined") {
    return;
  }
  let server_id = $("meta[name=server_id]").attr("content");
  let extension_id = $("meta[name=extension_id]").attr("content");
  let token = $("meta[name=csrf-token]").attr("content");

  var upload = new tus.Upload(option.file, {
    endpoint:
      "/upload?extension_id=" +
      extension_id +
      "&server_id=" +
      server_id +
      "&x-csrf-token=" +
      token,
    retryDelays: [0, 1000, 3000, 5000, 10000],
    overridePatchMethod: true,
    chunkSize: 1000 * 1000,
    resume: false,
    metadata: {
      filename: option.file.name,
      filetype: option.file.type,
    },
    onError: option.onError,
    onProgress: option.onProgress,
    onSuccess: function () {
      let url_parts = upload.url.split("/");
      let key = url_parts[url_parts.length - 1];
      let data = new FormData();
      data.append("key", key);
      request("/upload_info", data, function (response) {
        try {
          json = JSON.parse(response);
          upload.info = json;
          option.onSuccess(upload);
        } catch (e) {}
      });
    },
    headers: {
      server_id: server_id,
      extension_id: extension_id,
      "x-csrf-token": token,
    },
  });
  upload.start();
}
